// store.js
import Vuex from "vuex";

export default new Vuex.Store({
    state: {
        userId: null,  // Состояние для хранения userId
        token: null     // Состояние для хранения токена
    },
    mutations: {
        setUserId(state, userId) {
            state.userId = userId;
        },
        setToken(state, token) {
            state.token = token;
        }
    },
    actions: {
        login({ commit }, userId) {
            commit('setUserId', userId);
        },
        setToken({ commit }, token) {
            commit('setToken', token);
        }
    },
    getters: {
        getUserId(state) {
            return state.userId;
        },
        getToken(state) {
            return state.token;
        }
    }
});
