import { reactive } from "vue";

const store = reactive({
    cartItems: [],
    userId: sessionStorage.getItem('userId'),  // Get userId from sessionStorage

    // Function to load cart items for the current user
    async loadCart() {
        try {
            const response = await fetch(`http://localhost:8000/api/carts?user_id=${this.userId}`);
            if (!response.ok) {
                throw new Error("Не удалось загрузить корзину");
            }
            const data = await response.json();

            this.cartItems = data.map(cartItem => ({
                id: cartItem.id,
                title: cartItem.item.title,
                price: cartItem.item.price,
                size: cartItem.size,
            }));
        } catch (error) {
            console.error("Ошибка при загрузке корзины:", error);
        }
    },

    get totalPrice() {
        return this.cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
    },

    get totalItems() {
        return this.cartItems.reduce((count, item) => count + item.quantity, 0);
    },
});

export default store;
