// store.js
import Vuex from "vuex";

export default new Vuex.Store({
    state: {
        userId: null,  // Состояние для хранения userId
    },
    mutations: {
        setUserId(state, userId) {
            state.userId = userId;
        },
    },
    actions: {
        login({ commit }, userId) {
            commit('setUserId', userId);
        },
    },
    getters: {
        getUserId(state) {
            return state.userId;
        },
    }
});
